import React, { Component } from 'react'
import axios from 'axios'

 class Post extends Component {
     constructor(props) {
         super(props)

         this.state = {
           title:"",
           id:""
         }
     }



    handleChange = (e) => {
        this.setState({
            title : e.target.value
        })
    }
    handlePost = (e) => {

    e.preventDefault()
    console.log(this.state)

    axios.post("http://localhost:3000/posts", this.state)
        .then(res => console.log(res))
        .catch(error => console.log(error))
    window.location.reload(false)

    }
    handleChangeId = (e) => {
        this.setState({
            id : e.target.value
        })
    }
    handleRemove =(e) => {
        e.preventDefault()
        console.log(this.state)

    axios.delete("http://localhost:3000/posts${this.state.id} ")
        .then(res => console.log(res))
        .catch(error => console.log(error))
    window.location.reload(false)


    }



    render() {
        const{title} = this.state
        return (
            <div>
                <h1>Post to posts</h1>
                <form >
                    <div>
                        <input name="title" placeholder ="title" value={title}  onChange = {this.handleChange}></input>
                    </div>                    
                </form>
                <button onClick={this.handlePost}>Post to users</button>
                <h1>remove from posts</h1>
                <form >
                    <div>
                        <input name="id" placeholder ="id" value={title}  onChange = {this.handleChangeId}></input>
                    </div>                    
                </form>
                <button onClick = {this.handleRemove}>remove</button>
            </div>
        )
    }
}

export default Post
