         }
     }
     componentDidMount () {
        axios.get("http://localhost:3000/posts")
            .then(res => {
                console.log(res)
                this.setState({
                    post : res.data 
                })
            })
            .catch(error  => {
                console.log(error)
            })

     }

    render() {
        const{post}=this.state
